function constructAlarmAddressSpace(server, addressSpace, eventObjects, done) {
  // server = the created node-opcua server
  // addressSpace = address space of the node-opcua server
  // eventObjects = add event variables here to hold them in memory from this script

  // internal sandbox objects are:
  // node = the compact server node,
  // coreServer = core compact server object for debug and access to NodeOPCUA
  // this.sandboxNodeContext = node context node-red
  // this.sandboxFlowContext = flow context node-red
  // this.sandboxGlobalContext = global context node-red
  // this.sandboxEnv = env variables
  // timeout and interval functions as expected from nodejs

  const opcua = coreServer.choreCompact.opcua;
  const LocalizedText = opcua.LocalizedText;
  const namespace = addressSpace.getOwnNamespace();

  const Variant = opcua.Variant;
  const DataType = opcua.DataType;
  const DataValue = opcua.DataValue;

  var flexServerInternals = this;

  this.sandboxFlowContext.set("tank1Level", 0);
  this.sandboxFlowContext.set("tank2Level", 0);
  this.sandboxFlowContext.set("tank1Temperature", 0);
  this.sandboxFlowContext.set("tank1Humidity", 0);
  this.sandboxFlowContext.set("tank2Temperature", 0);
  this.sandboxFlowContext.set("tank2Humidity", 0);


  coreServer.debugLog("init dynamic address space");
  const rootFolder = addressSpace.findNode("RootFolder");

  node.warn("construct new address space for OPC UA");

  const tank1Folder = namespace.addFolder(rootFolder.objects, { "browseName": "Tank 1" });
  const tank2Folder = namespace.addFolder(rootFolder.objects, {"browseName": "Tank 2"});

  const tank1Unit1Components = namespace.addFolder(tank1Folder, { "browseName": "Tank 1 - Unit 1" });
  const tank1Unit2Components = namespace.addFolder(tank1Folder, {"browseName": "Tank 1 - Unit 2"});

  const tank2Unit1Components = namespace.addFolder(tank2Folder, { "browseName": "Tank 2 - Unit 1" });
  const tank2Unit2Components = namespace.addFolder(tank2Folder, {"browseName": "Tank 2 - Unit 2"});


  const tank1Level = namespace.addVariable({
    "organizedBy": tank1Unit1Components,
    "browseName": "Tank Level",
    "nodeId": "ns=1;s=Tank1_Level",
    "dataType": "Double",
    "value": {
      "get": function() {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("tank1Level")
        });
      },
      "set": function(variant) {
        flexServerInternals.sandboxFlowContext.set(
          "tank1Level",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

    const tank1Temperature = namespace.addVariable({
    "organizedBy": tank1Unit2Components,
    "browseName": "Tank Temperature",
    "nodeId": "ns=1;s=Tank1_Temperature",
    "dataType": "Double",
    "value": {
      "get": function() {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("tank1Temperature")
        });
      },
      "set": function(variant) {
        flexServerInternals.sandboxFlowContext.set(
          "tank1Temperature",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

    const tank1Humidity = namespace.addVariable({
    "organizedBy": tank1Unit2Components,
    "browseName": "Tank Humidity",
    "nodeId": "ns=1;s=Tank1_Humidity",
    "dataType": "Double",
    "value": {
      "get": function() {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("tank1Humidity")
        });
      },
      "set": function(variant) {
        flexServerInternals.sandboxFlowContext.set(
          "tank1Humidity",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

    const tank2Level = namespace.addVariable({
    "organizedBy": tank2Unit1Components,
    "browseName": "Tank Level",
    "nodeId": "ns=1;s=Tank2_Level",
    "dataType": "Double",
    "value": {
      "get": function() {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("tank2Level")
        });
      },
      "set": function(variant) {
        flexServerInternals.sandboxFlowContext.set(
          "tank2Level",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

    const tank2Temperature = namespace.addVariable({
    "organizedBy": tank2Unit2Components,
    "browseName": "Tank Temperature",
    "nodeId": "ns=1;s=Tank2_Temperature",
    "dataType": "Double",
    "value": {
      "get": function() {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("tank2Temperature")
        });
      },
      "set": function(variant) {
        flexServerInternals.sandboxFlowContext.set(
          "tank2Temperature",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

    const tank2Humidity = namespace.addVariable({
    "organizedBy": tank2Unit2Components,
    "browseName": "Tank Humidity",
    "nodeId": "ns=1;s=Tank2_Humidity",
    "dataType": "Double",
    "value": {
      "get": function() {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("tank2Humidity")
        });
      },
      "set": function(variant) {
        flexServerInternals.sandboxFlowContext.set(
          "tank2Humidity",
          parseFloat(variant.value)
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  coreServer.debugLog("create dynamic address space done");
  node.warn("construction of new address space for OPC UA done");

  done();
}
