module.exports = function (RED) {
  function booleanGenerator(config) {
    RED.nodes.createNode(this, config);
    var node = this;
    var truth = [true, false];
    var state = ["ON", "OFF"];
    var coin = ["Heads", "Tails"];

    node.on("input", function (msg) {
      var kind = eval(config.kind);
      msg.payload = kind[Math.round(Math.random())];
      node.send(msg);
    });
  }
  RED.nodes.registerType("Boolean", booleanGenerator);
};
